mapper.select:
	select 

  from user
  where is_delete=0 and login_id = #{loginId, jdbcType=VARCHAR} and password = #{password, jdbcType=VARCHAR}

select count(1)
from user
where is_delete=0 

and parent_id = #{parentId,jdbcType=VARCHAR}


and type = #{type,jdbcType=INTEGER}


and company_number =#{companyNumber,jdbcType=VARCHAR}


and region_id =#{regionId,jdbcType=VARCHAR}


 and login_id like '%${keyword}%'


and level =#{level,jdbcType=VARCHAR}

select

from user
where is_delete=0

and parent_id = #{parentId,jdbcType=VARCHAR}


and type = #{type,jdbcType=INTEGER}


and company_number =#{companyNumber,jdbcType=VARCHAR}


and region_id =#{regionId,jdbcType=VARCHAR}


 and login_id like '%${keyword}%'


and level =#{level,jdbcType=VARCHAR}

order by create_time desc

limit ${currentIndex},${pageSize}

mapper.update:
		update user set is_delete=1
		where user_id in

      #{id}
